CREATE TABLE UserDetails (    UserID INT IDENTITY(1,1) PRIMARY KEY,    FirstName VARCHAR(255),    MiddleName VARCHAR(255),    LastName VARCHAR(255),    FatherFirstName VARCHAR(255),    FatherMiddleName VARCHAR(255),    FatherLastName VARCHAR(255),    MotherFirstName VARCHAR(255),    MotherMiddleName VARCHAR(255),    MotherLastName VARCHAR(255),    Email VARCHAR(255),	ContactNumber VARCHAR(15),    Gender VARCHAR(10),    DateOfBirth DATE,    HighestEducation VARCHAR(255),    Branch VARCHAR(255),    YearOfPassout VARCHAR(255),    SecondarySchoolName VARCHAR(255),    HigherSecondarySchoolName VARCHAR(255),    BTechCollegeName VARCHAR(255),    MTechCollegeName VARCHAR(255),	SecondaryMarks VARCHAR(255),    HigherSecondaryMarks VARCHAR(255),    BTechMarks VARCHAR(255),    MTechMarks VARCHAR(255),	ProfilePhoto VARBINARY(max),	Aadharcard VARBINARY(max),	MyResume VARBINARY(max),	AboutMyself VARCHAR(255),    Hobbies VARCHAR(MAX));CREATE TABLE AddressDetails (    AddressID INT IDENTITY(1,1) PRIMARY KEY,	UserID Int,	AddressType Int,	Country VARCHAR(255),    StateField VARCHAR(255),    AddressField VARCHAR(255),    Pincode VARCHAR(255),	);create table Country(	CountryId Int IDENTITY(1,1) PRIMARY KEY,	CountryName varchar(255),)create table State(	StateId Int IDENTITY(1,1) PRIMARY KEY,	CountryId Int FOREIGN KEY REFERENCES Country(CountryID),	StateName varchar(255),)insert into Country(CountryName) values ('USA');insert into State(Countryid, Statename) values (1,'Odisha');insert into State(Countryid, Statename) values (2,'New York');select * from Country;select * from State;